import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { NotepadService } from '../notepad.service';
import { NotifierService } from 'angular-notifier';

@Component({
  selector: 'app-edit-notepad',
  templateUrl: './edit-notepad.component.html',
  styleUrls: ['./edit-notepad.component.scss']
})
export class EditNotepadComponent {
  action:string='';
  constructor(public fb:FormBuilder,public NotepadService:NotepadService,public router:Router,private notifier: NotifierService){
    this.NotepadService.DATA$.subscribe(data=>this.noteForm.setValue(data))
    this.NotepadService.ACTION$.subscribe(data=>this.action=data)
  }
  noteForm=this.fb.group({
    id:[0],
    title:['',Validators.required],
    date:['',Validators.required],
    describtion:['',Validators.required]
  })

   get noteFormControls(){
   return this.noteForm.controls
   }
  submit(){    
    if(this.action=='add'){
     this.NotepadService.addnotes(this.noteForm.value).subscribe(res=>{
       
     })
      this.notifier.notify('success', 'Add Sucessfully');
      this.router.navigate(['/notes'])
    }
    if(this.action=='edit'){
      let index:number=0
      this.NotepadService.EDIT$.subscribe((data:number)=>index=data)
      this.NotepadService.editnotes(this.noteForm.value).subscribe(res=>{
        console.log(res); 
       })
      this.notifier.notify('success', 'Edit Sucessfully');
      this.router.navigate(['/notes'])
    }

  }
  cancel(){
    this.router.navigate(['/notes'])
  }
}
<form [formGroup]="noteForm" (ngSubmit)="submit()" class="p-4">
        <div class="mb-3">
          <label for="title" class="form-label">Title</label>
          <input type="text" class="form-control" id="title" formControlName="title">
          <div *ngIf="noteFormControls?.title?.touched||noteFormControls?.title?.dirty" [ngClass]="'error'">
            <div *ngIf="noteFormControls?.title?.errors"
              class="alert py-1 fs-6 text-danger bg-transparent" role="alert">
              this Filed is Required
            </div>
          </div>
        </div>
        <div class="mb-3">
          <label for="title" class="form-label">Date</label>
          <input type="date" class="form-control" id="title" formControlName="date">
          <div *ngIf="noteFormControls?.date?.touched||noteFormControls?.date?.dirty" [ngClass]="'error'">
            <div *ngIf="noteFormControls?.date?.errors"
              class="alert py-1 fs-6 text-danger bg-transparent" role="alert">
              this Filed is Required
            </div>
          </div>
        </div>
        <div class="mb-3">
          <label for="description" class="form-label">Describtion</label>
          <textarea type="text" class="form-control" id="description" rows="10" formControlName="describtion"></textarea>
          <div *ngIf="noteFormControls?.describtion?.touched||noteFormControls?.describtion?.dirty" [ngClass]="'error'">
            <div *ngIf="noteFormControls?.describtion?.errors"
              class="alert py-1 fs-6 text-danger bg-transparent" role="alert">
              this Filed is Required
            </div>
          </div>
        </div>
        <div class="d-flex gap-2">
            <button type="submit" class="btn " *ngIf="action!='show'" [disabled]="!noteForm.valid">Submit</button>
            <button class="btn " (click)="cancel()">Cancel</button>
        </div>
      </form>
